import React, { useState, useEffect } from 'react';
import { Link, Route, Switch } from 'react-router-dom';
import logo from '../images/logo_header.svg';

function Header(props) {
    const [open, setOpen] = useState(false)

    const handleBtnClick = () => {
        const header = document.querySelector(".header");
        const headerElement = document.querySelector(".header__elements");
        const headerBox = document.querySelector(".header__box");
        const headerLogo = document.querySelector(".header__logo");
        const headerLink = document.querySelector(".header__link");
        const headerEmail = document.querySelector(".header__link-email");
        const boxBtn = document.querySelector(".header__box-btn");
        if (open === false) {
            boxBtn.classList.add("open");
            header.classList.add("header_burger-style")
            headerElement.classList.add("header__elements_burger-style")
            headerBox.classList.add("header__box_burger-style")
            headerLogo.classList.add("header__logo_burger-style")
            headerLink.classList.remove("header__link_hidden")
            headerEmail.classList.remove("header__link-email_hidden")
            headerEmail.classList.add("header__link-email_burger-style")
            setOpen(true);
        } else {
            boxBtn.classList.remove("open");
            header.classList.remove("header_burger-style")
            headerElement.classList.remove("header__elements_burger-style")
            headerBox.classList.remove("header__box_burger-style")
            headerLogo.classList.remove("header__logo_burger-style")
            headerLink.classList.add("header__link_hidden")
            headerEmail.classList.add("header__link-email_hidden")
            headerEmail.classList.remove("header__link-email_burger-style")
            setOpen(false);
        }
    }

    const handleBtnChange = () => {
        console.log('test')
        setOpen(false);
    }

    return (
        <header className="header ">
            <div className="header__elements">
            <Link to="/" className="header__logo-link" >
                <img src={logo} alt="Место Россиия" className="header__logo " />
            </Link>
            {props.loggedIn ?
            <div onClick={handleBtnClick} className="header__box-btn">
            <div className="header__box-btn-burger"></div>
            </div>
            : "" }
            </div>
            <div className="header__box ">
            <Switch>
                <Route path="/sign-up">
                {props.loggedIn ? "" : <Link className="header__link" to="/sign-in">Вход</Link>}
                </Route>
                <Route path="/sign-in">
                {props.loggedIn ? "" : <Link className="header__link" to="/sign-up">Регистрация</Link>}
                </Route>
                <Route path="/">
                <p className="header__link-email header__link-email_hidden">{props.email}</p>
                <Link className="header__link header__link_state header__link_hidden" to="/" onChange={handleBtnChange} onClick={props.onSignOut}>Выйти</Link>
                    </Route>
            </Switch>
            </div>
        </header>
    );
}

export default Header
